def extract_boxed_text(text: str) -> int:
    pattern = r'oxed{(.*?)}'
    matches = re.findall(pattern, text)
    if not matches:
        return -1
    try:
        content = matches[-1]
        num = int(content)
        return num % 1000
    except ValueError:
        try:
            num = int(float(content))
            if math.isinf(num):
                print(f"Parsed infinite value from {content}.")
            return num % 1000
        except (ValueError, OverflowError):
            return -1

