def pairwise_hinge_loss(scores, runtimes):
    loss = 0
    n = len(scores)
    for i in range(n):
        for j in range(n):
            if runtimes[i] < runtimes[j]:
                loss += F.relu(1 - (scores[i] - scores[j]))
    return loss / (n * (n - 1))

