function bidirectional_bfs(start, goal, allowed_moves):
    visited_from_start = {start}
    visited_from_goal = {goal}
    queue_start = Queue([(start, [])])
    queue_goal = Queue([(goal, [])])

    while not queue_start.empty() and not queue_goal.empty():
        # Expand from start
        state, path = queue_start.get()
        for move in allowed_moves:
            next_state = apply_move(state, move)
            if next_state in visited_from_goal:
                return path + [move] + reverse_path(visited_from_goal[next_state])
            if next_state not in visited_from_start:
                visited_from_start[next_state] = path + [move]
                queue_start.put((next_state, path + [move]))
        # Expand from goal (analogous)
        ...
    return None
// Beam search pseudocode
function beam_search(initial_state, allowed_moves, beam_width, heuristic):
    beams = PriorityQueue() // sorted by heuristic value
    beams.put((heuristic(initial_state), [initial_state], []))
    while not beams.empty():
        next_beams = PriorityQueue()
        for i in range(min(beam_width, beams.size())):
            _, states, moves = beams.get()
            current = states[-1]
            for move in allowed_moves:
                next_state = apply_move(current, move)
                new_moves = moves + [move]
                score = heuristic(next_state)
                if is_goal(next_state):
                    return new_moves
                next_beams.put((score, states + [next_state], new_moves))
        beams = next_beams
    return None

